/* rso TABLE */
DROP TABLE IF EXISTS rso;
CREATE TABLE rso (
	rsoId int AUTO_INCREMENT,
    rso_active boolean NOT NULL,
    rsoName varchar(255) NOT NULL UNIQUE,
    ownerId int NOT NULL,
    PRIMARY KEY (rsoId),
    FOREIGN KEY (ownerId) REFERENCES admin (adminId)
);
